import particlesConfig from "@/assets/particlesjs-config.json";
import Hero from "@/components/Hero.vue";
import Work from "@/components/Work.vue";
import MyNav from "@/components/MyNav.vue";
import About from "@/components/About.vue";
import CloseButton from "@/components/CloseButton.vue";
import DcmLawyers from "@/components/DcmLawyers.vue";
import ToTopButton from "@/components/ToTopButton.vue";
export default {
  name: "home",
  components: {
    Hero: Hero,
    Work: Work,
    MyNav: MyNav,
    About: About,
    CloseButton: CloseButton,
    DcmLawyers: DcmLawyers,
    ToTopButton: ToTopButton
  },
  data: function data() {
    return {
      activeSections: [false, false],
      //refactor this
      dcmIsActive: false,
      drivrIsActive: false,
      wellyMusicIsActive: false,
      activeProjects: {
        0: false,
        1: false,
        2: false
      },
      toTopActive: false // aboutIsActive: true,
      // contactIsActive: false,
      // workIsActive: false

    };
  },
  //   watch: {
  //     activeProjects: function(){
  //       toTopActive = true;
  // },
  mounted: function mounted() {
    var _this = this;

    require("particles.js");

    this.$nextTick(function () {
      _this.initParticlesJS();
    });
  },
  methods: {
    initParticlesJS: function initParticlesJS() {
      /* eslint-disable */
      particlesJS("particles-js", particlesConfig);
    },
    //checks to see if any of the states are active.
    //Returns true if any states are currently active.
    checkForActiveState: function checkForActiveState(array) {
      for (var i = 0; i < array.length; i++) {
        if (array[i]) {
          return true;
        }
      }
    },
    toTop: function toTop() {
      window.scrollTo({
        top: 0,
        left: 0,
        behavior: 'smooth'
      });
      this.toTopActive = false;
      this.activeProjects = {
        0: false,
        1: false,
        2: false
      };
    },
    //deals with active states for components from nav click
    clickHandler: function clickHandler(id) {
      var that = this;

      switch (id) {
        case "0":
          this.activeSections = [false, false];
          break;

        case "1":
          //if the clicked section is already active, minimise it
          if (that.activeSections[0]) {
            that.activeSections = [false, false]; //else check if any state is active, if true, remove active
            //set timeout for remove period, activate new state
          } else if (that.checkForActiveState(that.activeSections)) {
            that.activeSections = [false, false];
            setTimeout(function () {
              that.activeSections = [true, false];
            }, 500); //else if no state is active, active clicked state
          } else {
            that.activeSections = [true, false];
          }

          break;

        case "2":
          if (that.activeSections[1]) {
            that.activeSections = [false, false];
          } else if (that.checkForActiveState(that.activeSections)) {
            that.activeSections = [false, false];
            setTimeout(function () {
              that.activeSections = [false, true];
            }, 500);
          } else {
            that.activeSections = [false, true];
          }

          break;
      }
    },
    closeSections: function closeSections() {
      var nav = this.$refs.nav;
      this.clickHandler("0");
      nav.homeIsActive();
    },
    showProject: function showProject(id) {
      //refactor this
      var that = this;
      this.activeProjects = {
        0: false,
        1: false,
        2: false
      };
      this.toTopActive = false; // this.dcmIsActive = false;
      // this.drivrIsActive = false;
      // this.wellyMusicIsActive = false;

      switch (id) {
        case "0":
          this.activeProjects = {
            0: true,
            1: false,
            2: false
          }; //if it goes into a case, button will always be true

          this.toTopActive = true;
          break;

        case "1":
          this.activeProjects = {
            0: false,
            1: true,
            2: false
          };
          this.toTopActive = true;
          break;

        case "2":
          this.activeProjects = {
            0: false,
            1: false,
            2: true
          };
          this.toTopActive = true;
          break;
      }

      that.scrollToProject(); //double length of time of closing the top section for smooth scrolling experience

      setTimeout(function () {
        that.closeSections();
      }, 1000);
    },
    scrollToProject: function scrollToProject() {
      setTimeout(function () {
        document.getElementById("projectStart").scrollIntoView();
      }, 500);
    }
  }
};
